HacknetEditorHint.DynamicGenerateAttribute:
(function(actNode, hacknetNodeHolder){
    const attrs = [];
    const locales = ["en", "de", "fr", "ru", "es", "ko", "ja", "zh"];
    const desc = ["英文", "德语", "法语", "俄语", "西班牙语", "韩语", "日语", "中文"];
    for(const [key, val] of actNode.node.attribute) {
        locales.forEach((item, idx) => {
            attrs.push(`<Attribute name="${key}-${item}" required="false" desc="${key}属性的${desc[idx]}版本（来源:Hacknet-Extension-i18n Mod功能）" />`);
        });
    }

    return attrs.join("\n");
})
